import { useContext } from "react";
import { createContext } from "react";
import { useHistory } from "react-router";
import { toast } from "react-toastify";
import api from "../../services";
import { AuthenticatedContext } from "../authenticated";

export const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const { setAuthenticated } = useContext(AuthenticatedContext);
  const history = useHistory();

  const login = (data) => {
    api
      .post("/sessions/", data)
      .then((response) => {
        localStorage.setItem("@KenzieHealth:userName", data.username);
        localStorage.setItem(
          "@KenzieHealth:token",
          JSON.stringify(response.data.access)
        );
        toast.success("Login Feito com Sucesso!");
        setAuthenticated(true);
        useHistory.push("/dashboard");
      })
      .catch(() => {
        toast.error("Username ou senha inválidos!");
      });
  };

  const singUp = (data) => {
    api
      .post("/users/", data)
      .then(() => toast.success("Conta Criada Com Sucesso!"))
      .then(() => history.push("/login"))
      .catch(() => {
        toast.error("Erro ao criar sua conta!");
      });
  };

  return (
    <UserContext.Provider value={{ login, singUp }}>
      {children}
    </UserContext.Provider>
  );
};
