import styled from 'styled-components'
import background from '../../images/background-home.png'

export const Container = styled.div`
    width: 100vw;
    height: 100%;
    display: flex;
    flex-direction:column;
    justify-content: center;
    align-items: center;

    .name_page{
        width:218px;
        height:181px;
        font-size:1.5rem;
        position: absolute;
        left: 29px;
        top: 69px;
        border:3px solid blue;

    }

    .description{
        position: absolute;
        width: 286px;
        height: 84px;
        font-size:1.8rem;
        left: 30px;
        top: 284px;
        border:3px solid red;

    }

`
