import { StyleHamburguer } from "./style";
import { AiFillAppstore, AiFillCarryOut, AiFillSetting } from "react-icons/ai";
import { BsFillPeopleFill } from "react-icons/bs";
import { BiLogOut } from "react-icons/bi";
import k from "../../images/k.png";
import { useHistory } from "react-router";
import { useState, useEffect } from "react";
const Hamburguer = () => {
  const history = useHistory();
  const [userInfo, setUserInfo] = useState("");
  const userName = () => {
    setUserInfo(localStorage.getItem("@KenzieHealth:username"));
  };

  useEffect(() => {
    userName();
  }, [userName]);

  const logout = () => {
    localStorage.clear();
    history.push("/");
  };

  const local = history.location.pathname;

  return (
    <StyleHamburguer>
      <div>
        <img src={k} alt="" />
        <p>{userInfo}</p>
      </div>
      <div>
        <div
          style={
            local === "/dashboard"
              ? { backgroundColor: "var(--background)" }
              : { backgroundColor: "transparent" }
          }
        >
          <AiFillAppstore />
          <span onClick={() => history.push("/dashboard")}>Dashboard</span>
        </div>
        <div
          style={
            local === "/habits"
              ? { backgroundColor: "var(--background)" }
              : { backgroundColor: "transparent" }
          }
        >
          <AiFillCarryOut />
          <span onClick={() => history.push("/habits")}>Habits</span>
        </div>
        <div
          style={
            local === "/groups"
              ? { backgroundColor: "var(--background)" }
              : { backgroundColor: "transparent" }
          }
        >
          <BsFillPeopleFill />
          <span onClick={() => history.push("/groups")}>Groups</span>
        </div>
        <div
          style={
            local === "/settings"
              ? { backgroundColor: "var(--background)" }
              : { backgroundColor: "transparent" }
          }
        >
          <AiFillSetting />
          <span onClick={() => history.push("/settings")}>Setttings</span>
        </div>
      </div>
      <div>
        <div>
          <BiLogOut />
          <span onClick={() => logout()}>Logout</span>
        </div>
      </div>
    </StyleHamburguer>
  );
};

export default Hamburguer;
