import styled from "styled-components";

export const StyleCardCreate = styled.div`
  width: 300px;
  height: 350px;
  background-color: white;
  position: absolute;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  border-left: 25px solid var(--bar);
  form {
    height: inherit;
    display: flex;
    align-items: left;
    flex-direction: column;
    justify-content: space-around;
    padding: 20px;
    color: black;
    .title {
      color: black;
    }
    h2 {
      text-align: center;
    }
    svg {
      position: absolute;
      top: 0;
      right: 0;
      margin: 10px;
      color: red;
    }
  }
`;
