import styled from "styled-components";

export const StyleCard = styled.div`
  width: 100%;
  height: 800px;
  background-color: var(--background);
  border-radius: 20px;
  opacity: 0.6;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  overflow-y: scroll;
  padding: 20px;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  flex-wrap: wrap;
`;
