import Bar from "../../components/bar";
import Hamburguer from "../../components/hamburguer";
import { MainContainer, ContainerOne } from "./styles.js";
import { useContext, useState } from "react";
import Button from "../../components/button";
import CardCreate from "../../components/cardCreate";
import CardHabit from "../../components/cardHabit";
import { HabitListContext } from "../../Providers/habitsList";
const Habits = () => {
  const [showCard, setShowCard] = useState(false);
  const open = () => {
    setShowCard(!showCard);
  };
  const { habitList, removeHabit, createHabit, updateHabit } =
    useContext(HabitListContext);
  return (
    <>
      <Bar />
      <MainContainer>
        <div className="container">
          <div className="menu">
            <Hamburguer />
          </div>
          <div className="cards">
            <div className="card">
              <h1 className="title">Meus hábitos</h1>
              <Button onClick={() => open()}>Criar hábito</Button>
              {showCard && <CardCreate createHabit={createHabit} open={open} />}
              <ContainerOne>
                {habitList.map((item) => {
                  return (
                    <div className="one">
                      <CardHabit
                        item={item}
                        removeHabit={removeHabit}
                        updateHabit={updateHabit}
                      />
                    </div>
                  );
                })}
              </ContainerOne>
            </div>
          </div>
        </div>
      </MainContainer>
    </>
  );
};
export default Habits;
