import { useState } from "react";
import { TextField, InputAdornment, IconButton } from "@material-ui/core";
import { Countainer, Form, Poligon, AnimateDiv } from "./styles";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { useHistory, Link } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import api from "../../services";
import Button from "../../components/button";
import * as yup from "yup";
import Lottie from "react-lottie";
import animationData from "../../animation/animate-login";
import Bar from "../../components/bar";
import VisibilityIcon from "@material-ui/icons/Visibility";
import VisibilityOffIcon from "@material-ui/icons/VisibilityOff";
import { Redirect } from "react-router";
import { AuthenticatedContext } from "../../Providers/authenticated";
import { useContext } from "react";

const Login = () => {
  const [showPassword, setShowPassword] = useState("password");
  const { authenticated, setAuthenticated } = useContext(AuthenticatedContext);
  const history = useHistory();

  const schema = yup.object().shape({
    username: yup.string().required("Ensira seu username*"),
    password: yup.string().required("Ensira sua senha*"),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schema) });

  const handleForm = (data) => {
    api
      .post("/sessions/", data)
      .then((response) => {
        localStorage.setItem("@KenzieHealth:userName", data.username);
        localStorage.setItem(
          "@KenzieHealth:token",
          JSON.stringify(response.data.access)
        );
        toast.success("Login Feito com Sucesso!");
        setAuthenticated(true);
        history.push("/dashboard");
      })
      .catch((err) => {
        toast.error("Username / senha inválidos!");
      });
  };

  const [animationState, setAnimationState] = useState({
    isStopped: false,
    isPaused: false,
  });
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };

  if (authenticated) {
    return <Redirect to="/dashboard" />;
  }
  return (
    <>
      <Bar />
      <div style={{ display: "flex" }}>
        <AnimateDiv>
          <Lottie
            options={defaultOptions}
            height={"27vw"}
            width={"50vw"}
            speed={0.5}
            isStopped={animationState.isStopped}
            isPaused={animationState.isPaused}
          />
        </AnimateDiv>
        <Poligon></Poligon>
        <Countainer>
          <Form onSubmit={handleSubmit(handleForm)}>
            <h1>Login</h1>
            <div>
              <TextField
                label="Username"
                className="input"
                type="text"
                margin="normal"
                variant="outlined"
                color="primary"
                {...register("username")}
                helperText={errors.username?.message}
                error={!!errors.username}
              />
            </div>
            <div>
              <TextField
                label="Senha"
                className="input"
                type={showPassword}
                margin="normal"
                variant="outlined"
                color="primary"
                {...register("password")}
                helperText={errors.password?.message}
                error={!!errors.password}
                InputProps={{
                  endAdornment: (
                    <InputAdornment>
                      <IconButton
                        className="visibilityButton"
                        onClick={() =>
                          showPassword === "password"
                            ? setShowPassword("text")
                            : setShowPassword("password")
                        }
                        aria-label="toggle password visibility"
                      >
                        {showPassword === "password" ? (
                          <VisibilityOffIcon />
                        ) : (
                          <VisibilityIcon />
                        )}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
            </div>

            <Button>entrar</Button>
            <span>
              Nao tem cadastro?
              <Link to={"/signup"} className="link">
                Crie uma conta
              </Link>
            </span>
          </Form>
        </Countainer>
      </div>
    </>
  );
};

export default Login;
