import { StyleCardTask, StyleCardCreate } from "./styles";
import { AiOutlineCheckCircle, AiOutlineCloseCircle } from "react-icons/ai";
import { useState } from "react";
import { TextField } from "@material-ui/core";

const CardHabit = ({ item, removeHabit, updateHabit }) => {
  const [show, setShow] = useState(false);
  const open = () => {
    setShow(!show);
  };
  return (
    <StyleCardTask>
      <article>
        <span>Título: {item.title}</span>
        <span>Categoria: {item.category}</span>
        <span>Dificuldade: {item.difficulty}</span>
        <span>Frequência: {item.frequency}</span>
      </article>
      <div>
        <AiOutlineCheckCircle
          onClick={open}
          style={{ fontSize: "30px", color: "green", cursor: "poiter" }}
        />
        {show && (
          <StyleCardCreate>
            <TextField></TextField>
            <TextField></TextField>
          </StyleCardCreate>
        )}
        <AiOutlineCloseCircle
          onClick={() => removeHabit(item)}
          style={{ fontSize: "30px", color: "red", cursor: "poiter" }}
        />
      </div>
    </StyleCardTask>
  );
};

export default CardHabit;
