import Bar from "../../components/bar";
import Hamburguer from "../../components/hamburguer";
import { MainContainer } from "./styles.js";
import Card from "../../components/card";
import { useState } from "react";
import api from "../../services";
import { useEffect } from "react";

const Dashboard = () => {
  const [habbits, setHabbits] = useState([]);
  const [groups, setGroups] = useState([]);
  const [next, setNext] = useState("");

  const [token] = useState(
    JSON.parse(localStorage.getItem("@KenzieHabits:token")) || ""
  );

  const loadHabits = () => {
    api
      .get("habits/personal/", {
        headers: {
          Authorization: `Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNjM0NzUyMTk5LCJqdGkiOiJlNjVjODEyZjZlOGU0MjczYmI1ZjdhOTdjZGM5Y2U2NSIsInVzZXJfaWQiOjczfQ.osNoJX0iYIeMgqkNjExqysLaugPjHMmtzEOVENu4f4k`,
        },
      })
      .then((response) => setHabbits(response.data))
      .catch((err) => console.log(err));
  };
  const loadGroups = () => {
    api
      .get("/groups/", {
        headers: {
          Authorization: `Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNjM0NzUyMTk5LCJqdGkiOiJlNjVjODEyZjZlOGU0MjczYmI1ZjdhOTdjZGM5Y2U2NSIsInVzZXJfaWQiOjczfQ.osNoJX0iYIeMgqkNjExqysLaugPjHMmtzEOVENu4f4k`,
        },
      })
      .then((response) => {
        setGroups(response.data.results);
        setNext(response.data.next);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    loadHabits();
  });

  useEffect(() => {
    loadGroups();
  });

  console.log(habbits, groups);
  return (
    <>
      <Bar />
      <MainContainer>
        <div className="container">
          <div className="menu">
            <Hamburguer />
          </div>
          <div className="cards">
            <Card />
          </div>
          <div className="cards">
            <Card />
          </div>
        </div>
      </MainContainer>
    </>
  );
};

export default Dashboard;
