import Bar from "../../components/bar";
import Hamburguer from "../../components/hamburguer";
import { MainContainer } from "./styles.js";
import Button from "../../components/button";
import { useContext, useEffect } from "react";
import { ActivitiesContext } from "../../Providers/activities";
import { useParams } from "react-router";
import { GoalsContext } from "../../Providers/goals";
import api from "../../services";

const GroupsID = () => {
  const { id } = useParams();

  const token = `Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNjM0NzY2MTQzLCJqdGkiOiI4ZmFkOGU4ODU1OGI0ZGFiOGJlZGI1YWNhYTYxOWQwMiIsInVzZXJfaWQiOjE1fQ.MyM-dshWnP1BhPl-jbGWJGvTpe_ujZzKuEN1N6so-pY`;

  const { activitiesList, setActivitiesList } = useContext(ActivitiesContext);
  const { goalsList, setGoalsList } = useContext(GoalsContext);

  useEffect(() => {
    api
      .get(`/activities/?group=${id}&page=1`, {
        headers: {
          Authorization: token,
        },
      })
      .then((response) => {
        setActivitiesList(response.data.results);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      <Bar />
      <MainContainer>
        <div className="container">
          <div className="menu">
            <Hamburguer />
          </div>
          <div className="cards">
            <div className="card">
              <h1>Minhas Metas</h1>
              <Button>Criar meta</Button>
              {/* {goalsList.map((item) => {
                return <div>{item.title}</div>;
              })} */}
            </div>
            <div className="card">
              <h1>Minhas atividades</h1>
              <Button>Criar atividade</Button>
              {activitiesList.map((item) => {
                return <div>{item.title}</div>;
              })}
            </div>
          </div>
        </div>
      </MainContainer>
    </div>
  );
};

export default GroupsID;
