import Bar from "../../components/bar";
import Hamburguer from "../../components/hamburguer";
import { MainContainer } from "./styles.js";
import { useState } from "react";
import api from "../../services";
import { useEffect } from "react";
import CardTask from "../../components/cardTask";

const Dashboard = () => {
  const [habbits, setHabbits] = useState([]);
  const [groups, setGroups] = useState([]);
  const [next, setNext] = useState("");

  const [token] = useState(
    JSON.parse(localStorage.getItem("@KenzieHabits:token")) || ""
  );

  const loadHabits = () => {
    api
      .get("/habits/personal/", {
        headers: {
          Authorization: `Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNjM0NjAyMTM1LCJqdGkiOiJiZTVhZjJkODkyOWI0YWYwODZiODI3NmExYjE4NDIyOCIsInVzZXJfaWQiOjE1fQ.EYZCiQ0c-BM-LNoCDX5SFVtolZSBycFjrA_9fxyFVog`,
        },
      })
      .then((response) => setHabbits(response.data))
      .catch((err) => console.log(err));
  };

  const loadGroups = () => {
    api
      .get("/groups/subscriptions/", {
        headers: {
          Authorization: `Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNjM0NjAyMTM1LCJqdGkiOiJiZTVhZjJkODkyOWI0YWYwODZiODI3NmExYjE4NDIyOCIsInVzZXJfaWQiOjE1fQ.EYZCiQ0c-BM-LNoCDX5SFVtolZSBycFjrA_9fxyFVog`,
        },
      })
      .then((response) => {
        setGroups(response.data);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    loadHabits();
  }, []);

  console.log(groups);

  useEffect(() => {
    loadGroups();
  }, []);

  return (
    <>
      <Bar />
      <MainContainer>
        <div className="container">
          <div className="menu">
            <Hamburguer />
          </div>
          <div className="cards">
            {habbits.map((item) => {
              return <CardTask item={item} />;
            })}
          </div>
          <div className="cards">
            {groups.map((item) => {
              return <CardTask item={item} />;
            })}
          </div>{" "}
        </div>
      </MainContainer>
    </>
  );
};

export default Dashboard;
