import styled from "styled-components";

export const StyleCardTask = styled.div`
  max-width: 400px;
  width: 100%;
  max-height: 100px;
  height: 100%;
  background-color: var(--white);
  border-radius: 20px;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  display: flex;
  justify-content: center;
  align-items: center;
`;
