import { StyleCardCreate } from "./styles";
import { useForm } from "react-hook-form";
import Button from "../button/index";
import { Select, TextField } from "@material-ui/core";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { AiOutlineCloseCircle } from "react-icons/ai";
import { toast } from "react-toastify";

const CardCreate = ({ createHabit, open }) => {
  const formSchema = yup.object().shape({
    title: yup.string().required("Titulo obrigatório!"),
    category: yup.string().required("Categoria obrigatória!"),
    difficulty: yup.string().required("Dificuldade obrigatória!"),
    frequency: yup.string().required("Frequência obrigatória!"),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });

  const notify = () => toast.success("Seu habito foi criado com sucesso!");

  const onSubmit = (data) => {
    createHabit(data);
    open();
    notify();
  };

  const names = [
    "Oliver Hansen",
    "Van Henry",
    "April Tucker",
    "Ralph Hubbard",
    "Omar Alexander",
    "Carlos Abbott",
    "Miriam Wagner",
    "Bradley Wilkerson",
    "Virginia Andrews",
    "Kelly Snyder",
  ];

  return (
    <StyleCardCreate>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h1 className="title">Criar hábito</h1>
        <AiOutlineCloseCircle onClick={open} />
        <TextField
          {...register("title")}
          label="Titulo"
          type="text"
          helperText={errors.title?.message}
          error={!!errors.title}
        />
        <TextField
          {...register("category")}
          label="Categoria"
          type="text"
          helperText={errors.category?.message}
          error={!!errors.category}
        />
        <Select
          {...register("difficulty")}
          label="Dificuldade"
          type="text"
          helperText={errors.difficulty?.message}
          error={!!errors.difficulty}
        />
        <TextField
          {...register("frequency")}
          label="Frequência"
          type="text"
          helperText={errors.frequency?.message}
          error={!!errors.frequency}
        />
        <Button type="submit">Criar hábito</Button>
      </form>
    </StyleCardCreate>
  );
};

export default CardCreate;
