import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  width: 100%;
  height: 100%;
  justify-content: center;
  color: var(--white);
  font-size: 15px;
  h3 {
    font-size: 40px;
  }
  div {
    margin-top: 50px;
    background-color: var(--background);
    padding: 50px;
    width: 60%;
    height: 700px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    text-align: justify;
  }
`;
