import styled from "styled-components";

export const StyleCardCreate = styled.div`
  width: 300px;
  height: 450px;
  background-color: white;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  border-radius: 20px;
  display: ${(props) => (props.open ? "none" : "block")};
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  svg {
    position: absolute;
    top: 0;
    right: 0;
    margin: 10px;
    font-size: 30px;
    color: red;
    cursor: pointer;
  }
  form {
    height: inherit;
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: space-around;
    padding: 20px;
  }
`;
