import styled from "styled-components";

export const StyleBar = styled.div`
  width: 100%;
  height: 50px;
  background-color: var(--bar);
  display: flex;
  justify-content: space-between;
  color: var(--white);
  align-items: center;
  span {
    font-size: 45px;
    padding: 10px;
    cursor: pointer;
  }
  div {
    display: flex;
    span {
      font-size: 25px;

      cursor: pointer;
      margin: 10px;
      padding: 10px;
      border-left: 1px solid var(--black);
    }
  }
`;
