import styled from "styled-components";

export const MainContainer = styled.div`
  width: 100vw;
  height: calc(100vh - 60px);
  display: flex;
  justify-content: center;
  align-items: center;
  .container {
    width: 100vw;
    justify-content: space-space-around;
  }
  .menu {
    width: 300px;
    height: calc(100vh - 60px);
  }
  .cards {
    display: flex;
  }
`;
