import styled from "styled-components";

export const StyleBar = styled.div`
  width: 100%;
  height: 50px;
  background-color: var(--bar);
  display: flex;
  color: var(--white);
  align-items: center;
  div {
    max-width: 1366px;
    span {
      font-size: 40px;
      padding: 10px;
      cursor: pointer;
    }
    div {
      display: flex;
      span {
        font-size: 20px;

        cursor: pointer;
        margin: 10px;
        padding: 10px;
        border-left: 1px solid var(--black);
      }
    }
  }
`;
