import { StyleCardCreate } from "./styles";
import { useForm } from "react-hook-form";
import Button from "../button/index";
import { TextField } from "@material-ui/core";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { AiOutlineCloseCircle } from "react-icons/ai";
import { ToastContainer, toast } from "react-toastify";
import { useContext } from "react";
import { useParams } from "react-router";
import { GoalsContext } from "../../Providers/goals";
import "react-toastify/dist/ReactToastify.css";

const CardCreateGoals = ({ openShowGoals }) => {
  const { id } = useParams();

  const formSchema = yup.object().shape({
    title: yup.string(),
    difficulty: yup.string(),
    how_much_achieved: yup.number(),
  });

  const { createGoals } = useContext(GoalsContext);

  const {
    register,
    handleSubmit,
    // formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });

  const notify = () => toast.success("Wow so easy !");

  const onSubmit = ({ title, difficulty, how_much_achieved }) => {
    createGoals({
      title,
      difficulty,
      how_much_achieved,
      group: id,
    });
    openShowGoals();
    notify();
  };

  return (
    <StyleCardCreate>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h1 className="title">Criar atividade</h1>
        <AiOutlineCloseCircle onClick={openShowGoals} />
        <TextField {...register("title")} label="Title" type="text"></TextField>
        <TextField {...register("difficulty")} type="text"></TextField>
        <TextField {...register("how_much_achieved")} type="text"></TextField>
        <Button onClick={notify} type="submit">
          Criar Atividade
        </Button>
      </form>
      <ToastContainer />
    </StyleCardCreate>
  );
};

export default CardCreateGoals;
