import { StyleCardCreate } from "./styles";
import { useForm } from "react-hook-form";
import Button from "../button/index";
import { TextField } from "@material-ui/core";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { useHistory } from "react-router-dom";

const CardCreate = ({ createHabit }) => {
  const formSchema = yup.object().shape({
    name: yup
      .string()
      .required("Nome obrigatório")
      .matches("[A-Z][a-z].* [A-Z][a-z].*", "Deve haver somente letras"),
    email: yup
      .string()
      .required("E-mail obrigatório")
      .matches(
        "^[a-z0-9][-_.+!#$%&'*/=?^`{|]{0,1}([a-z0-9][-_.+!#$%&'*/=?^`{|]{0,1})*[a-z0-9]@[a-z0-9][-.]{0,1}([a-z][-.]{0,1})*[a-z0-9].[a-z0-9]{1,}([.-]{0,1}[a-z]){0,}[a-z0-9]{0,}$",
        "E-mail inválido"
      ),
    password: yup
      .string()
      .required("Senha obrigatória")
      .min(8, "A senha deve ter pelo menos 8 caracteres")
      .matches("(?=.*?[A-Z])", "Deve haver pelo menos uma letra maiúscula")
      .matches("(?=.*?[a-z])", "Deve haver pelo menos uma letra minúscula")
      .matches("(?=.*?[0-9])", "Deve haver pelo menos um dígito")
      .matches(
        "(?=.*?[#?!@$%^&*-])",
        "Deve haver pelo menos um caractere especial"
      )
      .matches(".{8,}", "Deve haver no mínimo oito caracteres"),
    confirmPassword: yup
      .string()
      .required("Confirmação da senha obrigatória")
      .oneOf([yup.ref("password"), null], "As senhas devem corresponder"),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });

  const onSubmit = (data) => {
    setLogado(true);
    history.push(`/home/${data.name}`);
    setObj(data);
  };

  return (
    <StyleCardCreate>
      <form onSubmit={handleSubmit(onSubmit)}>
        <TextField {...register("title")} label="Title" type="text"></TextField>
        <TextField
          {...register("category")}
          label="Category"
          type="text"
        ></TextField>
        <TextField
          {...register("difficulty")}
          label="Difficulty"
          type="text"
        ></TextField>
        <TextField
          {...register("frequency")}
          label="Frequency"
          type="text"
        ></TextField>
        <TextField
          {...register("how_much_achieved")}
          label="How much achieved"
          type="number"
        ></TextField>
        <Button type="submit"></Button>
      </form>
    </StyleCardCreate>
  );
};

export default CardCreate;
