import { StyleCardCreate } from "./styles";
import { useForm } from "react-hook-form";
import Button from "../button/index";
import { TextField } from "@material-ui/core";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { AiOutlineCloseCircle } from "react-icons/ai";
import toast from "react-hot-toast";

const CardCreateHabit = ({ open }) => {
  const formSchema = yup.object().shape({
    title: yup.string(),
    category: yup.string(),
    difficulty: yup.string(),
    frequency: yup.string(),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });

  const notify = () => toast.success("Habito criado com sucesso!");

  const onSubmit = (data) => {
    open();
    notify();
  };

  return (
    <StyleCardCreate>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h1 className="title">Criar Habito</h1>
        <AiOutlineCloseCircle onClick={open} />
        <TextField {...register("title")} label="Title" type="text"></TextField>
        <TextField
          {...register("category")}
          label="Category"
          type="text"
        ></TextField>
        <TextField
          {...register("difficulty")}
          label="Difficulty"
          type="text"
        ></TextField>
        <TextField
          {...register("frequency")}
          label="Frequency"
          type="text"
        ></TextField>
        <Button onClick={notify} type="submit">
          Criar Habito
        </Button>
      </form>
    </StyleCardCreate>
  );
};

export default CardCreateHabit;
