import Button from '../../components/button'
import { TextField} from '@material-ui/core'
import {useForm} from 'react-hook-form'
import {yupResolver} from '@hookform/resolvers/yup'
import { useHistory, Redirect } from 'react-router-dom'
import axios from 'axios'
import * as yup from 'yup'

const Login = () => {

  const history = useHistory()

  const linkTo = (path) => history.push(path)

  const schema = yup.object().shape({
    username: yup.string().required(''),
    password: yup
    .string()
    .required(''),

  })

  const { 
      register,
      handleSubmit,
      formState: {errors}
  } = useForm({resolver: yupResolver(schema)})

  const handleForm = (data) => {
    axios.post('https://kenziehub.herokuapp.com/sessions', data)
    .then((response) => {
        const{token, user} = response.data

        localStorage.setItem('@Doit:token', JSON.stringify(token))
        localStorage.setItem('@Doit:user', JSON.stringify(user.techs))
        localStorage.setItem('@Doit:id', JSON.stringify(user.id))

        return history.push('/dashboard')
    })
    .catch((err) => console.log(err.message)) 

}


  return (
    <div>
       <form onSubmit={handleSubmit(handleForm)}>
            <div>
                <TextField 
                    label='username'
                    margin='normal'
                    variant='outline'
                    color='primary'
                    {...register('username')}
                    error={!!errors.username}
                    helperText={errors.username?.message}
                    fullWidth
                />          
            </div>
            <div>
                <TextField 
                    label='Senha'
                    type='password'
                    margin='normal'
                    variant='filled'
                    color='primary'
                    {...register('password')}
                    error={!!errors.password}
                    helperText={errors.password?.message}
                    fullWidth
                />          
            </div>
            <div className='buttons'>
                <Button size="large" type='submit' variant='contained' color='primary'>
                    Login
                </Button>
                <Button size="large" variant='contained' color='warning' onClick={() => linkTo('/')}>
                    Voltar
                </Button>
            </div>
        </form>
        <TextField id="outlined-basic" label="Outlined" variant="outlined" />
        <input placeholder='username' type='text' />
        <input placeholder='senha' type='password' />
        <button>logar</button>
    </div>
  )
};

export default Login;
