import Bar from "../../components/bar";
import Hamburguer from "../../components/hamburguer";
import { MainContainer } from "./styles.js";
import Button from "../../components/button";
import { useContext } from "react";
import { ActivitiesProvider } from "../../Providers/activities";
import { useParams } from "react-router";
import { MyGroupListContext } from "../../Providers/myGroupList";

const GroupsID = () => {
  // const { searchActivities } = useContext(ActivitiesProvider);
  const { myGroupList } = useContext(MyGroupListContext);
  const { id } = useParams();
  const search = myGroupList.find((item) => item.id !== id);

  console.log(search);
  console.log(myGroupList);
  console.log(id);

  return (
    <>
      <Bar />
      <MainContainer>
        <div className="container">
          <div className="menu">
            <Hamburguer />
          </div>
          <div className="cards">
            <div className="card">
              <h1>Minhas Metas</h1>
              {search.goals.map((item) => {
                return <div>{item.title}</div>;
              })}
              <Button>Criar meta</Button>
            </div>
            <div className="card">
              <h1>Minhas atividades</h1>
              {search.activities.map((item) => {
                return <div>{item.title}</div>;
              })}
              <Button>Criar atividade</Button>
            </div>
          </div>
        </div>
      </MainContainer>
    </>
  );
};

export default GroupsID;
