import Bar from "../../components/bar";
import Hamburguer from "../../components/hamburguer";
import { MainContainer } from "./styles.js";
import { useJwt } from "react-jwt";
import { useState } from "react";
import api from "../../services";
import { useEffect } from "react";

const Settings = () => {
  const token = `Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNjM0NzY2MTQzLCJqdGkiOiI4ZmFkOGU4ODU1OGI0ZGFiOGJlZGI1YWNhYTYxOWQwMiIsInVzZXJfaWQiOjE1fQ.MyM-dshWnP1BhPl-jbGWJGvTpe_ujZzKuEN1N6so-pY`;
  const { decodedToken, isExpired } = useJwt(token);

  const { userInfo, setUserInfo } = useState("");

  useEffect(() => {
    api
      .get("/users/", {
        headers: {
          Authorization: token,
        },
      })
      .then((response) =>
        setUserInfo(response.data.results.filter((user) => user.id === 1))
      )
      .catch((err) => console.log(err));
  }, []);

  return (
    <>
      <Bar />
      <MainContainer>
        <div className="container">
          <div className="menu">
            <Hamburguer />
          </div>
          <div className="cards">
            <div className="card">
              <h1 className="title">Settings</h1>
              <ul>
                <li>Username: {userInfo.username}</li>
                <li>Email: {userInfo.email}</li>
              </ul>
            </div>
          </div>
        </div>
      </MainContainer>
    </>
  );
};

export default Settings;
