import styled from "styled-components";

export const StyleButton = styled.button`
  width: 250px;
  height: 50px;
  background-color: var(--button);
  border-radius: 20px;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  display: flex;
  justify-content: center;
  align-items: center;
  color: var(--white);

  span {
    font-size: 22px;
  }
`;
