import styled from "styled-components";

export const MainContainer = styled.div`
  width: 100vw;
  height: calc(100vh - 60px);
  display: flex;
  justify-content: space-around;
  align-items: center;
  .container {
    width: 100%;
    max-width: 1500px;
    height: 100%;
    display: flex;
    justify-content: space-space-around;
    align-items: center;
  }
  .menu {
    width: 20%;
    display: none;
    align-items: center;
    height: calc(100vh - 60px);
    @media (min-width: 900px) {
      display: flex;
    }
  }

  .cards {
    width: 80%;
    display: flex;
    justify-content: space-between;
  }

  .card {
    width: 500px;
    height: 800px;
    overflow-y: scroll;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 20px;
    background-color: var(--background);
    border-radius: 20px;
    padding: 20px 0;
  }
`;
