import { createContext, useContext } from "react";
import { useHistory } from "react-router";
import { toast } from "react-toastify";
import api from "../../services";
import { AuthenticatedContext } from "../authenticated";
export const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const history = useHistory();

  const constLogin = (data) => {
    api
      .post("/sessions/", data)
      .then((response) => {
        localStorage.setItem("@KenzieHealth:userName", data.username);
        localStorage.setItem(
          "@KenzieHealth:token",
          JSON.stringify(response.data.access)
        );
        toast.success("Login feito com ucesso!");
        history.push("/dashboard");
      })
      .catch(() => {
        toast.error("Username ou senha inválidos!");
      });
  };

  return (
    <UserContext.Provider value={{ constLogin }}>
      {children}
    </UserContext.Provider>
  );
};
