import { StyleCardTask } from "./styles";
import { AiOutlineCloseCircle } from "react-icons/ai";
import { useContext } from "react";
import { MyGroupListContext } from "../../Providers/myGroupList";

const CardActivities = ({ item }) => {
  const { subscribe } = useContext(MyGroupListContext);

  return (
    <StyleCardTask>
      <div>
        <span>Titulo: {item.title}</span>
        <span>
          Data:{" "}
          {item.realization_time
            .substring(0, 10)
            .split("-")
            .reverse()
            .join("/")}
        </span>
      </div>
      <div className="svg">
        <AiOutlineCloseCircle style={{ fontSize: "30px", color: "red" }} />
      </div>
    </StyleCardTask>
  );
};

export default CardActivities;
