import styled from "styled-components";

export const Area = styled.div`
  width: 100vw;
  height: 100vh;
`;

export const MainContainer = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  .Container {
    width: 100%;
    max-width: 1366px;
  }
`;
