import { StyleCardTask } from "./styles";
import { AiOutlineCloseCircle, AiOutlineCheckCircle } from "react-icons/ai";
import { useContext } from "react";
import { GoalsContext } from "../../Providers/goals";

const CardGoals = ({ item }) => {
  const { searchGoals, goalsList, createGoals, updateGoals, deleteGoals } =
    useContext(GoalsContext);

  const value = () => {
    if ((item.how_much_achieved * 100) / 30 < 100) {
      return (item.how_much_achieved * 100) / 30;
    } else {
      return 100;
    }
  };

  console.log(item);

  return (
    <>
      <StyleCardTask>
        <div>
          <span>Título: {item.title}</span>
          <span>Dificuldade: {item.difficulty}</span>
          <span>Quanto conquistado: {item.how_much_achieved}</span>
          <span>
            Alcançou: {item.achieved === true ? "Ativo" : "Desativado"}
          </span>
        </div>
        <div className="actions">
          <AiOutlineCloseCircle
            onClick={() => deleteGoals(item)}
            style={{ fontSize: "30px", color: "red", marginRight: "7px" }}
          />

          <div
            className="active"
            onClick={() => {
              updateGoals(item.how_much_achieved + 1, item.id);
            }}
          >
            <h3>{item.how_much_achieved} Dias</h3>
            <AiOutlineCheckCircle
              style={{ fontSize: "30px", color: "green" }}
            />
          </div>
        </div>
      </StyleCardTask>
    </>
  );
};

export default CardGoals;
