import { StyleCardTask, StyleCardCreate } from "./styles";
import { AiOutlineCloseCircle } from "react-icons/ai";
import { FiEdit } from "react-icons/fi";
import { useState } from "react";
import { TextField } from "@material-ui/core";
import Button from "../button";

const CardHabit = ({ item, removeHabit, updateHabit }) => {
  const [show, setShow] = useState(false);
  const open = () => {
    setShow(!show);
  };
  return (
    <>
      <StyleCardTask>
        <div>
          <span>Título: {item.title}</span>
          <span>Categoria: {item.category}</span>
          <span>Dificuldade: {item.difficulty}</span>
          <span>Frequência: {item.frequency}</span>
        </div>
        <div>
          <FiEdit
            onClick={open}
            style={{ fontSize: "30px", color: "blue", cursor: "poiter" }}
          />

          <AiOutlineCloseCircle
            onClick={() => removeHabit(item)}
            style={{ fontSize: "30px", color: "red", cursor: "poiter" }}
          />
        </div>
      </StyleCardTask>
      {show && (
        <StyleCardCreate>
          <div>
            <h2>Editar hábito</h2>
            <AiOutlineCloseCircle
              onClick={() => open()}
              style={{ fontSize: "30px", color: "red", cursor: "poiter" }}
            />
          </div>
          <TextField></TextField>
          <TextField></TextField>
          <Button>Editar</Button>
        </StyleCardCreate>
      )}
    </>
  );
};

export default CardHabit;
